import React from 'react';
import '../styles/About.css';

const About = () => {
    return (
        <div className="about-container">
            <h1>About Us</h1>
            <p>Welcome to MyStore, your number one source for all things tech. We're dedicated to providing you the very best of electronics, with an emphasis on quality, customer service, and uniqueness.</p>
            <p>Founded in 2021 by John Doe, MyStore has come a long way from its beginnings in a home office. When John first started out, his passion for eco-friendly tech products drove him to start his own business.</p>
            <p>We hope you enjoy our products as much as we enjoy offering them to you. If you have any questions or comments, please don't hesitate to contact us.</p>
            <p>Sincerely,<br />John Doe, CEO</p>
        </div>
    );
};

export default About;
