import React, { useState } from 'react';
import '../styles/amazon.css';
import Cards from './Cards';

const Amazon = ({ handleClick }) => {
    const [products] = useState([
        {
            id: 1,
            name: "Laptop",
            brand: "Brand A",
            price: 50000,
            img: "https://via.placeholder.com/150?text=Laptop",
        },
        {
            id: 2,
            name: "Smartphone",
            brand: "Brand B",
            price: 20000,
            img: "https://via.placeholder.com/150?text=Smartphone",
        },
        {
            id: 3,
            name: "Headphones",
            brand: "Brand C",
            price: 3000,
            img: "https://via.placeholder.com/150?text=Headphones",
        },
        {
            id: 4,
            name: "Smartwatch",
            brand: "Brand D",
            price: 10000,
            img: "https://via.placeholder.com/150?text=Smartwatch",
        },
        // Add more products as needed
    ]);

    const [filter, setFilter] = useState('');
    const [sort, setSort] = useState('');

    const handleFilterChange = (e) => {
        setFilter(e.target.value);
    };

    const handleSortChange = (e) => {
        setSort(e.target.value);
    };

    const filteredProducts = products.filter((product) =>
        product.name.toLowerCase().includes(filter.toLowerCase())
    );

    const sortedProducts = filteredProducts.sort((a, b) => {
        if (sort === 'price-asc') {
            return a.price - b.price;
        } else if (sort === 'price-desc') {
            return b.price - a.price;
        }
        return 0;
    });

    return (
        <div className="amazon-container">
            <div className="filter-sort">
                <input
                    type="text"
                    placeholder="Filter by name"
                    value={filter}
                    onChange={handleFilterChange}
                />
                <select value={sort} onChange={handleSortChange}>
                    <option value="">Sort by</option>
                    <option value="price-asc">Price: Low to High</option>
                    <option value="price-desc">Price: High to Low</option>
                </select>
            </div>
            <div className="product-list">
                {sortedProducts.map((product) => (
                    <Cards key={product.id} item={product} handleClick={handleClick} />
                ))}
            </div>
        </div>
    );
};

export default Amazon;
