import React from 'react';
import '../styles/About.css';

const About = () => {
    return (
        <div className="about-container">
            <h1>About Us</h1>
            <p>Welcome to Yofti-Store, your number one source for all things tech. We're dedicated to giving you the very best of electronics, with a focus on dependability, customer service, and uniqueness.</p>
            <p>Founded in 2024, Store has come a long way from its beginnings. When we first started out, our passion for providing the best equipment drove us to do intense research, and gave us the impetus to turn hard work and inspiration into a booming online store.</p>
            <p>We hope you enjoy our products as much as we enjoy offering them to you. If you have any questions or comments, please don't hesitate to contact us.</p>
       
        </div>
    );
};

export default About;
