import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Navbar from './components/Navbar'; // Ensure this path is correct
import Amazon from './components/Amazon'; // Ensure this path is correct
import Cart from './components/Cart'; // Ensure this path is correct
import ProductDetail from './components/ProductDetail'; // Ensure this path is correct
import './styles/app.css'; // Ensure this path is correct

const App = () => {
    // State to manage cart items
    const [cart, setCart] = useState([]);

    // Function to add an item to the cart
    const handleAddToCart = (item) => {
        setCart([...cart, { ...item, amount: 1 }]);
    };

    // Function to remove an item from the cart by its id
    const handleRemoveFromCart = (id) => {
        setCart(cart.filter(item => item.id !== id));
    };

    // Function to change the amount of a cart item
    const handleChangeAmount = (item, delta) => {
        const updatedCart = cart.map(cartItem => {
            if (cartItem.id === item.id) {
                return { ...cartItem, amount: cartItem.amount + delta };
            }
            return cartItem;
        }).filter(cartItem => cartItem.amount > 0);
        setCart(updatedCart);
    };

    return (
        <Router>
            <Navbar cartCount={cart.length} />
            <div className="app-container">
                <Routes>
                    <Route path="/" element={<Amazon handleClick={handleAddToCart} />} />
                    <Route path="/cart" element={<Cart cart={cart} setCart={setCart} handleChange={handleChangeAmount} handleRemove={handleRemoveFromCart} />} />
                    <Route path="/products/:id" element={<ProductDetail handleClick={handleAddToCart} />} />
                </Routes>
            </div>
        </Router>
    );
};

export default App;
