import React from 'react';
import '../styles/cards.css';

const Cards = ({ items, handleClick, handleViewDetails }) => {
    return (
        <div className="cards-container">
            {items.map((item) => {
                const { id, title, author, price, img } = item;
                return (
                    <div className="card" key={id}>
                        <div className="image-box">
                            <img src={img} alt={title} />
                        </div>
                        <div className="details">
                            <h2 className="title">{title}</h2>
                            <p className="author">{author}</p>
                            <p className="price">Price - {price} Rs</p>
                            <div className="button-group">
                                <button className="btn add-to-cart" onClick={() => handleClick(item)}>Add to Cart</button>
                                <button className="btn view-details" onClick={() => handleViewDetails(item)}>View Details</button>
                            </div>
                        </div>
                    </div>
                );
            })}
        </div>
    );
}

export default Cards;
