import React from 'react';
import '../styles/cards.css';

const Cards = ({ item, handleClick, handleViewDetails }) => {
    const { title, author, price, images } = item; // Change 'img' to 'images'

    return (
        <div className="card">
            <div className="image-gallery">
                {images.map((img, index) => (
                    <div className="image-box" key={index}>
                        <img src={img} alt={`${title} image ${index + 1}`} />
                    </div>
                ))}
            </div>
            <div className="details">
                <h2 className="title">{title}</h2>
                <p className="author">{author}</p>
                <p className="price">Price - {price} Rs</p>
                <div className="button-group">
                    <button className="btn add-to-cart" onClick={() => handleClick(item)}>Add to Cart</button>
                    <button className="btn view-details" onClick={() => handleViewDetails(item)}>View Details</button>
                </div>
            </div>
        </div>
    );
}

export default Cards;
